sp_widget.client_script:
function($scope, PWGanttConfig, PCService, $rootScope) {
	/* widget controller */
	var c = this;

	$scope.savePreferenceAndSettings = function(mode){
		if (mode == 'beforestart')
			PWGanttConfig.MODE = "introduction";
		else PWGanttConfig.MODE = "";
		PCService.savePreference('INTRO', true, true)
			.then(function() {
			PWGanttConfig.SETTINGS.INTRO = true;
		});
		if(mode != 'beforestart')
			$rootScope.$broadcast('walkthrough.closed');
	};
}

sp_widget.link:
function(scope, ele, attr) {
	var PWGanttInstance = $injector.get('PWGanttInstance'),
		PWConstants = $injector.get('PWConstants'),
		PWConfig = $injector.get('PWConfig'),
		ProjectGanttWalkthroughSteps = $injector.get('ProjectGanttWalkthroughSteps'),
		$window = $injector.get('$window'),
		$timeout = $injector.get('$timeout'),
		PWGanttConfig = $injector.get('PWGanttConfig');

	var gantt = PWGanttInstance.get(PWConstants.GANTT_INSTANCE);
	if(gantt && typeof gantt.scrollTo === 'function') gantt.scrollTo(0, 0);
	if(PWConfig.entity === PWConstants.ENTITY.PROJECT) {
		scope.steps = ProjectGanttWalkthroughSteps.getSteps();

		var callout = new Callout({
			steps: scope.steps,
			callbacks: {
				beforeStart: _closeMenuAndSavePreference,
				onExit: _closeMenuAndSavePreference,
				onComplete: _closeMenuAndSavePreference
			},
			welcomeScreen: {
				intro: "<b>" + scope.data.messages.planningConsole + "</b><br/>" +
						scope.data.messages.pcOverView + scope.data.messages.liveFeed,
				introShort: scope.data.messages.introShort
			},
			startLabel: scope.data.messages.startLabel,
			nextLabel: scope.data.messages.nextLabel,
			doneLabel: scope.data.messages.doneLabel
		});

		$timeout(function() {
			callout.start(PWGanttConfig && PWGanttConfig.SETTINGS && PWGanttConfig.SETTINGS.showWelcomeScreen);
		}, 0);

	} 

	function _closeMenuAndSavePreference(mode) {
		scope.savePreferenceAndSettings(mode);
		$('body').trigger('click');
	}
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.messages = {};
	data.messages.planningConsole = gs.getMessage('Planning Console');
	data.messages.pcOverView = gs.getMessage('The planning console gives project managers a comprehensive view of all aspects of a project, including a hierarchical list of sub-projects, if any, project tasks, which appear in a work breakdown structure (WBS) list with task assignments, and the project Gantt chart. ');
	data.messages.liveFeed = gs.getMessage('The console also integrates with Live Feed so your users can collaborate on projects.');
	data.messages.introShort = gs.getMessage('Introduction to Planning Console.');
	data.messages.startLabel = gs.getMessage('Take the tour');
	data.messages.nextLabel = gs.getMessage('Got it');
	data.messages.doneLabel = gs.getMessage('Done');
})();
