sys_script_include.script:
var RunbookStateHandlerAjaxICE = Class.create();
RunbookStateHandlerAjaxICE.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	initialize: function(request, responseXML, gc) 
	{
        AbstractAjaxProcessor.prototype.initialize.call(this, request, responseXML, gc);
        this.log = new GSLog(RunbookStateHandlerICE.LOG_PROPERTY);
        this.log.setLog4J();
    },

    /**
     * Confirms whether the state value is a valid "next" state for the change to move to
     * 
     * @param sysparm_change_sys_id sys_id of a runbook record
     * @param sysparm_next_state name of the state to check. e.g. initial, draft, approved etc.
     */
    ajaxFunction_isNextState: function() {
        var sysId = this.getParameter("sysparm_runbook_sys_id");
        var runbookGr = this._getRunbookGr(sysId);
        if (!runbookGr) {
            this.log.debug("[isNextState] could not find Runbook with sys_id = " + sysId);
            return false;
        }

        return new RunbookStateHandler(changeGr).isNext(this.getParameter("sysparm_next_state"));
    },

    /**
     * Returns an array containing the values of the next states for an existing runbook
     * 
     * @param sysparm_change_sys_id sys_id of a runbook record
     */
    ajaxFunction_getNextStateValues: function() {
        var runbookId = this.getParameter("sysparm_change_sys_id");
		var runbookType = '' + this.getParameter("sysparm_runbook_type");
        if (!runbookId && !runbookType)
            return "";

		var gr;
		gr = new GlideRecord("runbook");
		if (!gr.get(runbookId)) {
			this.log.debug("[getNextStateValues] could not find Runbook with sys_id = " + changeId);
			gr = Runbook.newRunbook(runbookType).getGlideRecord();
		} 	

		var stateValues = [];

		var stateHandler = new RunbookStateHandler(gr);
		var nextStates = stateHandler.getNextStates();
		if (nextStates) {
			for (var i = 0; i < nextStates.length; i++)
				stateValues.push(stateHandler.getStateValue(nextStates[i]));
		}

		return JSON.stringify(stateValues);
    },

    /**
     * Returns the name of the state given its numeric value. E.g. -4 = assess, -3 = authorize
     * 
     * @param sysparm_state_name name of the state to convert. E.g. assess, authorize, etc
     */
    ajaxFunction_getStateValue: function() {
        var stateName = this.getParameter("sysparm_state_name");
        if (!stateName)
            return "";

        var stateValue = new RunbookStateHandler().getStateValue(stateName);

        this.log.debug("[getStateValue] state with name '" + stateName + "' return with value '" + stateValue + "'");

        return stateValue || "";
    },

    _getRunbookGr: function(sysId) {
        if (!sysId)
            return null;

        var gr = new GlideRecord("runbook");
        if (gr.get(sysId))
            return gr;

        return null;
    },

    type: 'RunbookStateHandlerAjaxICE'
});
