sys_script_include.script:
var RunbookUsageAnalytics = Class.create();
RunbookUsageAnalytics.prototype = {
    initialize: function() {
    },
	runbookConsoleLaunch: function(data1, data2, data3) {
		this.logAnalytics('runbookconsole', 'open', data1, data2, data3);
	},

	deferedModeSave: function(data1, data2) {
		this.logAnalytics('runbookconsole', 'deferedSave', data1, data2);
	},

	runbookWorkbenchLaunch: function(data1, data2) {
		this.logAnalytics('runbookworkbench', 'open', data1, data2);
	},

	runbookStatusPortalLaunch : function(module) {
		this.logAnalytics('runbookstatusportal', 'open', module);
	},

	runbookprintGantt : function(entity, sysClassName) {
		this.logAnalytics('runbookprintgantt', 'open', entity, sysClassName);
	},

	runbookmsProjectImport: function(source) {
		this.logAnalytics('runbookmsprojectimport', 'open', source);
	},
	runbookWorkspaceLaunch: function() {
		this.logAnalytics('runbook_workspace', 'load');
	},
	menuClick: function(menuName) {
		this.logAnalytics('landing_page_menu', 'click', menuName);
	},
	cardClick: function() {
		this.logAnalytics('workspace_card', 'click');
	},
	runbookOnClient: function(name, value) {
		this.logAnalytics('runbookconsole', name, value);
	},
	logAnalytics: function(eventType, action, data1, data2, data3) {
		if ( AnalyticsFramework.isDisabled() )
			return;
		var streamId = "ice.now.runbook_management";
		var obfuscationList = [];
		status = AnalyticsFramework.open(streamId);
		var data = {};
		data["data.1"] = '';
		data["data.2"] = '';
		data["data.3"] = '';
		data["event.type"] = eventType;
		data["action"] = action;
		data["app.name"] = "runbook_management";
		if ( data1 )
			data["data.1"] = data1;
		if ( data2 )
			data["data.2"] = data2;
		if ( data3 )
			data["data.3"] = data3;
		if (status === 0) {
			status = AnalyticsFramework.sendJSON(streamId, obfuscationList, JSON.stringify(data));
			status = AnalyticsFramework.close(streamId);
		}
	},

    type: 'RunbookUsageAnalytics'

};
